INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000007028, 'My mind... my flesh... I\'m... rotting... !', '아아아... 내 영혼... 내 살이... 썩어 들어가고 있어...!', 'Mon esprit... ma chair... Je suis en train de... pourrir... !', 'Mein Verstand... mein Fleisch... ich... verwese...!', '我的思想……我的血肉……我在腐烂……！', '我的精神...我的血肉...我開始腐爛了!', 'Mi mente... mi carne... ¡Me... estoy... pudriendo!', 'Mi mente... mi carne... ¡Me... estoy... pudriendo!', 'Мой разум... моя плоть... я... гнию!');
