INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000124, '%s begins chanting as he mixes the well stones and ambercorn before the Tribal Fire.', '%s|1이;가; 부족의 불 앞에서 우물돌과 대지열매를 결합하면서 주문을 외우기 시작합니다.', '%s se met à psalmodier en mélangeant les pierres du puits et les pommes de pin avant d\'allumer le Feu du clan.', '%s beginnt zu singen, während er die Quellsteine und das Bernsteinkorn vor dem Stammesfeuer vermischt.', '%s站在部族之火前，把井石与琥珀颗粒混合起来，然后开始咏唱咒语。', '%s在部族之火前，一面混合水井石與琥珀顆粒，一面詠唱咒語。', '%s comienza a cantar a la vez que mezcla las piedras del pozo con las bellotas ámbar ante el Fuego Tribal.', '%s comienza a cantar a la vez que mezcla las piedras del pozo con las bellotas ámbar ante el Fuego Tribal.', '%s начинает произносить заклинание, смешивая камни, найденные у источника, и янтарное семя перед костром племени.');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000125, 'The Water of the Seers is ready for your consumption, $n.', '그대가 마실 현자의 물이 준비되었네.', 'L’Eau des Prophètes est prête à être bue, $n.', 'Das Wasser der Seher steht für Euch zum Trinken bereit, $n.', '先知之水已经为你准备好了，$n。', '先知之水已經為你準備好了，$n。', 'El agua de los videntes está lista para su consumo, $n.', 'El agua de los videntes está lista para su consumo, $n.', 'Вода Провидцев готова, $n. Можешь пить.');
