INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000100, '%s begins to manipulate the Stone of Relu over the two fossils.', '두 개의 화석 위로 %s|1이;가; 렐루의 돌을 이리저리 맞춰 보기 시작합니다.', '%s commence à manipuler la Pierre de Relu au-dessus des deux fossiles.', '%s beginnt, den Stein von Relu über den beiden Fossilien zu benutzen.', '%s开始摆弄两块化石上的雷鲁之石。', '%s開始在這兩具化石上調整雷魯之石。', '%s comienza a manipular la Piedra de Relu sobre los dos fósiles.', '%s comienza a manipular la Piedra de Relu sobre los dos fósiles.', '%s начинает водить камнем Релу над двумя окаменелостями.');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000101, 'By the stars! A spirit has been summoned!', '별의 힘으로! 아... 영혼이 소환되었소!', 'Par les étoiles ! Un esprit a été invoqué !', 'Bei den Sternen! Es wurde ein Geist beschworen!', '以群星的名义！', '以群星的名義!靈魂已經被召喚了!', '¡Por las estrellas! ¡Se ha invocado a un espíritu!', '¡Por las estrellas! ¡Se ha invocado a un espíritu!', 'Клянусь звездами! Дух призван!');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000102, 'Who hath summoned forth Aman?', '이 아만을 이곳으로 부른 자가 누구인가?', 'Qui a invoqué Aman ?', 'Wer hat Aman heraufbeschworen?', '谁召唤了阿曼？', '誰召喚了阿曼?', '¿Quién ha invocado a Aman?', '¿Quién ha invocado a Aman?', 'Кто призвал Амана?');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000103, 'Ah, I see you toil with relics of the past.', '그대들은 과거의 비밀을 밝히려 애쓰고 있구나.', 'Ah, je vois que vous avez du mal avec les reliques du passé.', 'Ah, Ihr beschäftigt Euch mit den Relikten der Vergangenheit, wie ich sehe.', '啊，你终于带着那些古物回来了。', '啊，你終於帶著那些古物回來了。', 'Ah, veo que trabajas arduamente con las reliquias del pasado.', 'Ah, veo que trabajas arduamente con las reliquias del pasado.', 'А, вижу, вы старательно работаете с древностями.');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000104, 'Be warned that even your creators are fallible.', '경고하건대 그대를 창조한 자도 잘못을 범할 수 있다.', 'Prenez garde, même vos créateurs sont faillibles.', 'Seid gewarnt, auch Eure Schöpfer sind fehlbar.', '注意，即使是创物之神也可能犯错。', '注意，即使是創物之神也可能犯錯。', 'Te advierto que incluso tus creadores pueden equivocarse.', 'Te advierto que incluso tus creadores pueden equivocarse.', 'Помните, что даже ваши создатели небезупречны.');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000105, 'Digging too deep into your past might bring an abrupt end to your future.', '과거를 너무 깊이 들추다 보면 앞날에 갑작스러운 최후를 맞이할 수도 있느니.', 'À fouiller trop loin dans votre passé, vous risquez de voir votre avenir prendre fin d\'une manière abrupte.', 'Grabt Ihr zu tief in Eurer Vergangenheit, könnte das Eurer Zukunft ein abruptes Ende bringen.', '对你的过去了解得越多，你的未来就越危险。', '對你的過去了解得越多，你的未來就越危險。', 'Si escarbas demasiado en tu pasado, tu futuro puede tener un fin repentino.', 'Si escarbas demasiado en tu pasado, tu futuro puede tener un fin repentino.', 'Если слишком глубоко закопаться в прошлое, можно повредить своему будущему.');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000106, '%s dissipates before your eyes.', '%s|1이;가; 눈앞에서 사라집니다.', '%s se volatilise sous vos yeux.', '%s löst sich vor Euren Augen auf.', '%s在你眼前突然消失。', '%s在你眼前突然消失。', '%s se disipa ante tus ojos.', '%s se disipa ante tus ojos.', '%s исчезает у вас на глазах.');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000107, 'It\'s a mystery of the past indeed! But a key to our future!', '아까 우리가 본 것은 참으로 과거의 신비라 하지 않을 수 없소! 동시에 미래에 대한 중요한 열쇠이기도 하오!', 'C’est un mystère du passé, pour sûr ! Mais aussi une clé pour notre avenir !', 'Es ist in der Tat ein Rätsel aus der Vergangenheit! Aber auch ein Schlüssel zu unserer Zukunft!', '过去的事情谁也说不清，但重要的是我们的未来！', '過去的事情誰也說不清，但重要的是我們的未來!', '¡Está claro que es un misterio del pasado! ¡Pero también la llave de nuestro futuro!', '¡Está claro que es un misterio del pasado! ¡Pero también la llave de nuestro futuro!', 'Это воистину тайна прошлого! Но и ключ к нашему будущему!');
