INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000041, 'Farewell, my friend.', '잘 가게, 친구.', 'Adieu, mon ami.', 'Lebt wohl, mein Freund.', '保重，我的朋友。', '保重，我的朋友。', 'Adiós, amigo mío.', 'Adiós, amigo mío.', 'До встречи, мой друг.');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000040, 'And thank you, $N.  Without your aid I may never have found my way to the Forsaken.', '그리고 고맙네, $n. 자네 도움이 없었다면 결코 포세이큰의 길로 접어들지 못했을 걸세.', 'Et merci, $n. Sans votre aide, je n\'aurais peut-être jamais trouvé le chemin qui mène aux Réprouvés.', 'Und danke, $n. Ohne Eure Hilfe hätte ich den Weg zu den Verlassenen nie gefunden.', '谢谢你，$n。没有你的帮助，我永远无法成为被遗忘者的一员。', '謝謝你，$n。沒有你的幫助，我永遠無法成為被遺忘者的一員。', 'Y gracias, $n. Sin tu ayuda, jamás habría encontrado el camino hasta los Renegados.', 'Y gracias, $n. Sin tu ayuda, jamás habría encontrado el camino hasta los Renegados.', 'Да, и спасибо тебе, $n. Без твоей помощи я бы никогда не нашел Отрекшихся.');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000039, 'When time permits, we must speak at length.  For we have much to discuss.', '언제 시간 날 때 더 얘기 나누세. 할 얘기가 너무 많다네.', 'Quand nous en aurons le temps, il faudra que nous parlions longuement. Car nous avons beaucoup à nous dire.', 'Wenn es die Zeit erlaubt, dann müssen wir uns in Ruhe unterhalten, denn wir haben viel zu besprechen.', '如果时间允许的话，我们得好好谈一下。因为我们有很多事情要讨论。', '如果時間允許的話，我們得好好談一下。因為我們有很多事情要討論。', 'Si el tiempo lo permite tenemos que hablar con calma. Hay mucho que discutir.', 'Si el tiempo lo permite tenemos que hablar con calma. Hay mucho que discutir.', 'Надо будет поговорить подольше, когда время позволит. Нам есть что обсудить.');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000038, 'It has been a long time, Bethor, my friend.', '오랜만일세, 베소르, 내 친구여.', 'Cela fait longtemps, Bethor, mon ami.', 'Es ist lange her, Bethor, mein Freund.', '比索，我的朋友，好久不见啊。', '比索，我的朋友，好久不見啊。', 'Cuanto tiempo, mi querido amigo Bethor.', 'Cuanto tiempo, mi querido amigo Bethor.', 'Давненько мы не виделись, Бетор, друг мой.');
