INSERT INTO `script_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1780228, 'Keep it up, $n, and I\'ll beat some manners into you.', '계속해 보시지, $n. 본때를 보여줄 테니까.', 'Continuez comme ça, $n, et je vous rouerai de coups pour vous apprendre les bonnes manières.', 'Macht weiter so, $n, und ich werde Euch Manieren einprügeln.', '小心我狠狠地收拾你一顿。', '給我認真點，$n，否則我會狠狠教訓你一頓。', 'Anímate, $n, y te inculcaré modales a golpes.', 'Anímate, $n, y te inculcaré modales a golpes.', 'Продолжай в том же духе, $n, – и скоро я тебя кое-чему научу.', 0, 0, 7, 5, 'Master Wood - On Rude Emote 2');
