INSERT INTO `creature_ai_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1215, 'Well hello, $n, what can I get you today?', '어서 오십시오. 오늘은 어떤 걸로 준비해 드릴까요?', 'Bonjour, $n, que puis-je vous offrir aujourd’hui ?', 'Hallo, $n, was kann ich Euch heute bringen?', '你好，$n，我能为你做些什么吗？', '你好，$n，我能為你做些什麼嗎?', 'Hola, $n, ¿qué deseas que te traiga hoy?', 'Hola, $n, ¿qué deseas que te traiga hoy?', 'Привет, $n! Что тебе подать сегодня?', 0, 0, 0, 0, 'Jenn/Elly Langston random text');
INSERT INTO `creature_ai_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1216, 'Been a tough day? A nice ale should loosen those worries right up.', '힘든 하루였나요? 좋은 에일 맥주 한 잔이면 근심이 싹 사라지실 거예요.', 'Rude journée ? Une bonne bière devrait pouvoir chasser immédiatement tous ces soucis.', 'Hattet Ihr einen harten Tag? Ein gutes Bier sollte die Sorgen fortspülen.', '劳累了一整天？上好的啤酒可以为您解除疲劳。', '勞累了一整天?上好的麥芽酒可以為您解除疲勞。', '¿Has tenido un día difícil? Una buena cerveza disiparán todas esas preocupaciones.', '¿Has tenido un día difícil? Una buena cerveza disiparán todas esas preocupaciones.', 'Был тяжелый денек? Хороший эль разгонит все печали.', 0, 0, 0, 0, 'Jenn/Elly Langston random text');
INSERT INTO `creature_ai_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1218, 'What do you fancy, $G sir:miss;?', '무엇을 원하시나요, 손님?', 'Qu’est-ce qui vous fait envie, $Gmonsieur:madame; ?', 'Was wünscht Ihr, $GSire:meine Dame;?', '有什么可以帮忙的吗，$G先生:小姐;？', '有什麼可以幫忙的嗎，$G先生:小姐;?', '¿Qué te apetece, $gseñor:señorita;?', '¿Qué te apetece, $gseñor:señorita;?', 'Что вас интересует, $gсэр:леди;?', 0, 0, 0, 0, 'Jenn Langston random text');
INSERT INTO `creature_ai_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1219, 'Hi, what would you like?', '어서 오세요. 뭘 드릴까요?', 'Salut, que désirez-vous ?', 'Hallo, was möchtet Ihr?', '嗨，你想要什么？', '嗨，你想要什麼?', 'Hola, ¿qué te apetece?', 'Hola, ¿qué te apetece?', 'Привет, чего ищем?', 0, 0, 0, 0, 'Jenn Langston random text');
INSERT INTO `creature_ai_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1220, 'Look what the cat dragged in. What can I get you, $n?', '오, 많이 피곤하셨던 모양이군요.. 뭘 준비해 드릴까요, 손님?', 'Regardez ce que le chat a apporté. Que voulez-vous, $n ?', 'Schaut nur, wer da kommt. Was kann ich Euch bringen, $n?', '看看谁来了……我能为你做点什么吗，$n？', '看看誰來了...我能為你做點什麼嗎，$n?', 'Mira quién ha venido. ¿Qué te traigo, $n?', 'Mira quién ha venido. ¿Qué te traigo, $n?', 'Да уж, чего только народ на себя не понацепит! Гм, чем могу помочь, $n?', 0, 0, 0, 0, 'Jenn Langston random text');
