REPLACE INTO `script_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1500000, 'DARKSHIRE...I HUNGER!!', '어둠골... 난 배가 고프다!!', 'SOMBRE-COMTÉ... J’AI FAIM !!', 'DUNKELHAIN... ICH HABE HUNGER!!!', '夜色镇……我饿了！！', '夜色鎮...我餓了!!', '¡¡VILLA OSCURA!!... ¡¡TENGO HAMBRE!!', '¡¡VILLA OSCURA!!... ¡¡TENGO HAMBRE!!', 'ТЕМНОЛЕСЬЕ... ХОЧУ ЕСТЬ!', 8593, 1, 0, 0, 'Stitches');
INSERT INTO `script_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1500001, 'ROARRRRR!!', '크아아아!!', 'GROARRRRR !!', 'RRRRRRRR!!!', '呼啊啊啊啊啊啊啊啊！！', '呼啊啊啊啊啊啊啊啊!!', '¡¡GRRRRRRR!!', '¡¡GRRRRRRR!!', 'Р-Р-Р-Р-Р!', 0, 1, 0, 0, 'Stitches');
INSERT INTO `script_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1500002, 'Rouse and to arms, citizens of Darkshire! An abomination of the undead approaches along the road!', '어둠골의 시민들이여, 일어나 전투 준비를 하십시오! 혐오스러운 언데드가 곧 쳐들어올 것입니다!', 'Aux armes, habitants de Sombre-Comté ! Une abomination des morts-vivants s’approche sur la route !', 'Erhebt euch und greift zu den Waffen, Bürger von Dunkelhain! Eine Monstrosität der Untoten nähert sich auf der Straße!', '夜色镇的居民们，拿起你们的武器！一只亡灵憎恶正沿着大道走过来，妄图进犯我们的家园！', '夜色鎮的居民們，拿起你們的武器!一隻亡靈憎惡體正沿著大道走過來，妄圖進犯我們的家園!', '¡Despertad y coged las armas, ciudadanos de Villa Oscura! ¡Una abominación de los no-muertos se aproxima por el camino!', '¡Despertad y coged las armas, ciudadanos de Villa Oscura! ¡Una abominación de los no-muertos se aproxima por el camino!', 'К оружию, жители Темнолесья! Жуткая нежить, Поганище, приближается по дороге!', 0, 1, 7, 5, 'Darkshire Town Crier');
INSERT INTO `script_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1500003, 'The abomination has overrun the Night Watch camp! Quickly, we must intercept it before it reaches town!', '혐오스러운 언데드가 어둠의 순찰대 야영지를 덮쳤습니다! 놈이 마을에 도착하기 전에 어서 저지해야 합니다!', 'L’abomination a envahi le camp des Veilleurs ! Vite, nous devons l’intercepter avant qu’elle n’atteigne la ville !', 'Die Monstrosität ist in das Lager der Nachtwache eingefallen! Schnell, wir müssen sie abfangen, bevor sie die Stadt erreicht!', '那个憎恶已经毁灭了我们守夜人的营地！快点，我们必须在它到达城镇之前阻止它！', '那隻憎惡體已經毀滅了我們守夜人的營地!快點，我們必須在它到達城鎮之前阻止它!', '¡La abominación ha arrasado el campamento de La Guardia Nocturna! ¡Rápido, debemos interceptarla antes de que llegue a la ciudad!', '¡La abominación ha arrasado el campamento de La Guardia Nocturna! ¡Rápido, debemos interceptarla antes de que llegue a la ciudad!', 'Поганище уже в лагере Ночного Дозора! Скорее, мы должны остановить его, пока он не дошел до города!', 0, 1, 7, 5, 'Darkshire Town Crier');
INSERT INTO `script_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1500004, 'It isn\'t enough! Defenders, gather in the center of town. Together we will stand against the undead monster!', '병력이 부족합니다! 모든 수비대는 마을 중앙에 집결해 주십시오. 모두 다 함께 언데드에 맞서 싸웁시다!', 'Ce n’est pas suffisant ! Que les défenseurs se rassemblent au centre de la ville ! Nous ferons front ensemble contre le monstre mort-vivant !', 'Das ist nicht genug! Verteidiger, versammelt Euch in der Mitte der Stadt. Zusammen werden wir dem untoten Monster standhalten!', '快一点！勇敢的卫士们，快到镇中心集合！我们要并肩击退亡灵！', '快一點!勇敢的防衛者們，快到鎮中心集合!我們要並肩擊退亡靈怪物!', '¡No es suficiente! Reuníos en el centro de la ciudad. ¡Juntos combatiremos al monstruo no-muerto!', '¡No es suficiente! Reuníos en el centro de la ciudad. ¡Juntos combatiremos al monstruo no-muerto!', 'Этого недостаточно. Защитники, собирайтесь в центре города. Вместе мы выстоим против этой нежити!', 0, 1, 7, 5, 'Darkshire Town Crier');
INSERT INTO `script_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1500005, 'The abomination has come! Forward!', '놈이 왔습니다! 전원 전투 태세를 갖추십시오!', 'L\'abomination est arrivée ! En avant !', 'Die Monstrosität ist hier! Vorwärts!', '憎恶来了！冲啊！', '憎惡怪來了!衝啊!', '¡La abominación ha llegado! ¡Adelante!', '¡La abominación ha llegado! ¡Adelante!', 'Поганище уже здесь! Вперед!', 0, 1, 7, 5, 'Darkshire Town Crier');
INSERT INTO `script_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1500006, 'The beast is slain! All\'s well in Darkshire!', '괴물이 죽었습니다! 이제 어둠골은 안전합니다!', 'La bête est morte ! Tout va bien dans Sombre-Comté !', 'Die Bestie wurde erschlagen! Jetzt herrscht wieder Ruhe in Dunkelhain!', '憎恶被杀死了！夜色镇安全了！', '憎惡怪被殺死了!夜色鎮安全了!', '¡Han matado a la bestia! ¡Todo va bien en Villa Oscura!', '¡Han matado a la bestia! ¡Todo va bien en Villa Oscura!', 'Чудовище повержено! В Темнолесье все спокойно!', 0, 1, 7, 5, 'Darkshire Town Crier');
INSERT INTO `script_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1500007, 'Beware!  Beware!  A threat lurks in the wild!  Night Watchers, be on the alert!', '주의하라! 주의하라! 어둠골 외곽에서 심각한 위협이 다가오고 있다! 어둠의 순찰대는 경계 태세를 강화하라!', 'Prenez garde ! Prenez garde ! Une menace rôde aux alentours ! Veilleurs, soyez sur vos gardes !', 'Achtung! Achtung! Gefahr lauert in der Wildnis! Nachtwachen, seid auf der Hut!', '注意！注意！夜色镇面临巨大的威胁！守夜人，保持警戒！', '注意!注意!夜色鎮面臨巨大的威脅!守夜人，保持警戒!', '¡Cuidado! ¡Cuidado! ¡Una amenaza acecha entre la maleza! ¡Miembros de La Guardia Nocturna, estad alerta!', '¡Cuidado! ¡Cuidado! ¡Una amenaza acecha entre la maleza! ¡Miembros de La Guardia Nocturna, estad alerta!', 'Берегитесь! Берегитесь! Угроза таится в глуши! Тревога, Ночной Дозор!', 0, 1, 7, 0, 'Watcher Cutford');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000135, 'Yes, commander!', '예, 사령관님!', 'Oui, commandant !', 'Ja, Kommandant!', '遵命，长官！', '遵命，長官!', '¡Sí, Comandante!', '¡Sí, Comandante!', 'Да, командир!');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000134, 'This doesn\'t bode well.  Send scouts to gather more intelligence, and stay on alert.', '좋지 않은 징조야. 순찰대원을 보내 정보를 수집하고 경계를 강화하게.', 'Ce n’est pas de bon augure. Envoyez des éclaireurs afin de rassembler plus d’informations et restez vigilants.', 'Das verheißt nichts Gutes. Schickt Späher aus, um mehr Informationen zu sammeln, und bleibt wachsam.', '这不是个好兆头。马上派人去继续侦查，其他人保持警戒。', '這不是個好兆頭。馬上派斥候前去繼續偵察，其他人保持警戒。', 'No es buena señal. Envía exploradores para que aumenten el equipo de inteligencia y manteneos alerta.', 'No es buena señal. Envía exploradores para que aumenten el equipo de inteligencia y manteneos alerta.', 'Это не предвещает ничего хорошего. Отправьте разведчиков и будьте начеку.');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000133, 'Althea, there are reports of strange happenings at the graveyard.  Ello believes it portends an attack on Darkshire.', '알시아 님, 묘지에서 이상한 일들이 일어나고 있다는 보고가 들어왔습니다. 시장께서는 어둠골에 대한 공격의 전조라 믿고 계십니다.', 'Althea, on signale des événements étranges au cimetière. Ello pense que cela laisse présager une attaque sur Sombre-Comté.', 'Althea, ich habe gehört, dass auf dem Friedhof seltsame Dinge vor sich gehen sollen. Ello denkt, dass es Anzeichen für einen Angriff auf Dunkelhain sein könnten.', '阿尔泰娅，有报告说墓地里有异常情况。艾尔罗认为这是夜色镇即将遭到攻击的前兆。', '奧爾希雅，有報告說墓地裡有異常情況。艾爾羅認為這是夜色鎮即將遭到攻擊的前兆。', 'Althea, se dice que están ocurriendo cosas extrañas en el cementerio. Ello cree que esto presagia un posible ataque sobre Villa Oscura.', 'Althea, se dice que están ocurriendo cosas extrañas en el cementerio. Ello cree que esto presagia un posible ataque sobre Villa Oscura.', 'Альтея, на кладбище происходит что-то странное. Элло считает, что Темнолесью грозит опасность.');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000132, 'Yes, Lord Mayor.', '예, 시장님.', 'Oui, messire maire.', 'Ja, Oberbürgermeister.', '遵命，大人。', '遵命，鎮長大人。', 'Sí, señor alcalde.', 'Sí, señor alcalde.', 'Да, господин мэр.');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000131, 'Watcher, inform Althea that a dire threat is marshalling against our town.  We must prepare!', '순찰대원, 알시아 사령관에게 마을에 심각한 위협이 다가오고 있음을 보고 드리게. 어서 준비해야 하네!', 'Surveillant, informez Althea qu’une menace terrible marche sur notre ville. Nous devons être prêts !', 'Behüter, informiert Althea, dass eine entsetzliche Bedrohung auf unsere Stadt zusteuert. Wir müssen Vorbereitungen treffen!', '快派人去告诉阿尔泰娅，我们的小镇面临着巨大的危险。我们必须做好准备！', '快派人去告訴奧爾希雅，我們的小鎮面臨著巨大的危險。我們必須做好準備!', 'Vigía, avisa a Althea que una siniestra amenaza se cierne sobre nuestra ciudad. ¡Debemos prepararnos!', 'Vigía, avisa a Althea que una siniestra amenaza se cierne sobre nuestra ciudad. ¡Debemos prepararnos!', 'Страж, передай Альтее, что на город надвигается страшная угроза. Мы должны подготовиться!');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000130, 'This is grim news, and I fear it is linked to the dealings $n had with the Embalmer.', '안 좋은 소식이군. 이것이 $n|1이;가; 장의사를 위해 한 일과 관련이 있는 것이 아닐까 우려되는군.', 'C’est une sombre nouvelle et je crains qu’elle soit liée aux trafics entre $n et l’Embaumeur.', 'Das sind schlimme Nachrichten und ich fürchte, dass sie etwas mit den Geschäften zu tun haben, die $n mit dem Einbalsamierer macht.', '这是一个可怕的消息，我担心这与$n为那个老隐士做的事情有关。', '這是一個可怕的消息，我擔心這與$n為那個藏屍者做的事情有關。', 'Son malas noticias, y me temo que están relacionadas con los tratos que tuvo $n con el Embalsamador.', 'Son malas noticias, y me temo que están relacionadas con los tratos que tuvo $n con el Embalsamador.', 'У меня плохие новости. Боюсь, они связаны с делами, которые $n и Бальзамировщик вели между собой.');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000129, 'Wolves head this way, as if fleeing from...something.', '늑대들이 무언가에 쫓기듯이 이쪽으로 도망치고 있습니다.', 'Les loups se dirigent par ici, comme s’ils fuyaient… quelque chose.', 'Wölfe sind in diese Richtung unterwegs, als würden sie... vor etwas fliehen.', '狼群都在朝这边跑，好像是为了躲避……躲避什么东西……', '狼群都在朝這邊跑，好像是在逃避...什麼東西...', 'Los lobos van en esa dirección, como si huyeran de... algo.', 'Los lobos van en esa dirección, como si huyeran de... algo.', 'Волки все бегут туда, будто от чего-то... спасаются.');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000128, 'Hideous bellows and growls are heard coming from that old hermit\'s shack!', '저 늙은 은둔자의 통나무집에서 끔찍한 포효와 으르렁거리는 소리가 들리고 있습니다!', 'D’horribles mugissements et grognements proviennent de la hutte de ce vieil ermite !', 'Grauenhaftes Gebrüll und Geknurre dringt aus der Hütte des alten Einsiedlers!', '从那个老隐士的小屋里传出了可怕的咆哮声！', '從那個老隱士的小屋裡傳出了可怕的咆哮聲!', '¡Se escuchan espantosos bramidos y gruñidos procedentes de la choza del viejo ermitaño!', '¡Se escuchan espantosos bramidos y gruñidos procedentes de la choza del viejo ermitaño!', 'Из лачуги старого отшельника слышен страшный рев и рычание!');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000127, 'Lord Mayor!  Our scouts report strange activity to the west.', '시장님! 정찰병의 보고에 따르면 서쪽에서 이상한 움직임이 있다고 포착되었다고 합니다.', 'Messire maire ! Nos éclaireurs nous signalent des activités étranges à l’ouest.', 'Oberbürgermeister! Unsere Späher berichten von seltsamen Bewegungen im Westen.', '大人！我们的卫兵报告说西边有一些异常的情况。', '鎮長大人!我們的斥候回報西邊有一些異常的情況。', '¡Señor alcalde! Nuestros exploradores han registrado extraños movimientos en el oeste.', '¡Señor alcalde! Nuestros exploradores han registrado extraños movimientos en el oeste.', 'Господин мэр! Разведчики докладывают о странных происшествиях на западе.');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000126, 'Make Way!  Make Way!', '길을 내주시오! 어서 비키시오!', 'Laissez passer ! Laissez passer !', 'Macht Platz! Macht Platz!', '让一让！让一让！', '讓一讓!讓一讓!', '¡Abrid paso! ¡Abrid paso!', '¡Abrid paso! ¡Abrid paso!', 'Дорогу! Дорогу!');
