INSERT INTO `script_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1001019, 'You are an agent of their wicked god, fool.  I will see you destroyed!', '그들의 사악한 신을 섬기는군. 어리석은 것. 널 없애버리고 말겠다!', 'Vous êtes un agent de leur dieu pernicieux, espèce de fou. Je vais vous détruire !', 'Ihr seid ein Agent ihres tückischen Gottes, Dummkopf! Ich werde Euch vernichten lassen!', '你是邪恶之神的奴仆，愚蠢的家伙。我要见证你的毁灭！', '你是邪惡之神的奴僕，愚蠢的傢伙。我要見證你的毀滅!', 'No eres $gun:una; representante de su malvado dios, idiota. ¡Contemplaré tu destrucción!', 'No eres $gun:una; representante de su malvado dios, idiota. ¡Contemplaré tu destrucción!', 'Ты – слуга их безумного бога! Сейчас я увижу твою смерть!', 0, 0, 0, 0, 'Chained Essence of Eranikus');
INSERT INTO `script_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1001018, 'My soul is not a trinket!  Mortal, you must release me from these chains!', '나의 영혼은 장신구가 아니다! 이 구속에서 날 풀어다오!', 'Mon âme n\'est pas un jouet ! Mortel, vous devez me libérer de ces chaînes !', 'Meine Seele ist kein Stück billigen Plunders! Sterbliches Wesen, Ihr müsst mich von diesen Ketten befreien!', '我的灵魂不是用来当装饰品的！凡人，你必须将我从枷锁中释放出来！', '我的靈魂至關重要!你必須將我從枷鎖中釋放出來!', '¡Mi alma no es ninguna baratija! Mortal, ¡tienes que liberarme de estas cadenas!', '¡Mi alma no es ninguna baratija! Mortal, ¡tienes que liberarme de estas cadenas!', 'Моя душа – не безделушка! $GСмертный, ты должен:Смертная, ты должна; избавить меня от этих оков!', 0, 0, 0, 0, 'Chained Essence of Eranikus');
INSERT INTO `script_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1001017, 'My charge to watch the temple has failed... my corrupted soul knows no peace...', '신전을 지키는 임무에는 실패하고... 내 타락한 영혼은 편히 쉴 수가 없구나...', 'J\'ai échoué dans ma mission de surveillance du temple... Mon âme corrompue ne connaît pas de repos...', 'Ich habe meinen Auftrag zur Bewachung des Tempels nicht erfüllt und nun findet meine verderbte Seele keine Ruhe...', '我没能守护好神殿……我堕落的灵魂将永远无法平静……', '我沒能守護好神殿...我墮落的靈魂將永遠無法平靜...', 'No he cumplido mi cometido de proteger el templo... mi corrupta alma no tiene paz...', 'No he cumplido mi cometido de proteger el templo... mi corrupta alma no tiene paz...', 'Я не выполнил свой долг по присмотру за храмом... моя загубленная душа не знает покоя...', 0, 0, 0, 0, 'Chained Essence of Eranikus');
INSERT INTO `script_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1001016, 'Foolish mortal, I will rend your soul in two once I am released!', '바보 같으니! 내가 풀려나면 네 영혼을 갈기갈기 찢어놓겠다!', 'Mortel, arrêtez cette folie... Je déchirerai votre âme en deux dès que je serai libre !', 'Wie töricht, sterbliches Wesen! Ich werde Eure Seele in Stücke reißen, wenn ich frei bin!', '愚蠢的凡人，只要我被释放出来，我就要撕碎你的灵魂！', '愚蠢的凡人，只要我被釋放出來，我就要撕碎你的靈魂!', '¡Iluso mortal, partiré tu alma en dos en cuanto me libere!', '¡Iluso mortal, partiré tu alma en dos en cuanto me libere!', '$gГлупый смертный:Глупая смертная;, я разорву твою душу надвое, как только освобожусь!', 0, 0, 0, 0, 'Chained Essence of Eranikus');
INSERT INTO `script_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1001015, 'My eyes!  It burns... it burns...', '내 눈! 불에 타는구나... 불에 타는구나...', 'Mes yeux ! Ça brûle... Ça brûle...', 'Meine Augen! Das brennt! Das brennt!', '我的眼睛！它在燃烧……它在燃烧……', '我的眼睛!它在燃燒...它在燃燒...', '¡Mis ojos! Arden... arden...', '¡Mis ojos! Arden... arden...', 'Мои глаза! Жжет... жжет...', 0, 0, 0, 0, 'Chained Essence of Eranikus');
INSERT INTO `script_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1001014, 'Torture me no more!  Release me, mortal!  The Swamp of Sorrows... mortal... please...', '더 이상 고통스럽게 하지 마라! 나를 해방시켜 다오! 슬픔의 늪... 제발...', 'Arrêtez de me torturer ! Relâchez-moi, mortel ! Le marais des Chagrins... mortel... je vous en prie...', 'Quält mich nicht länger! Lasst mich frei, sterbliches Wesen! Die Sümpfe des Elends... sterbliches Geschöpf... bitte...', '不要再烦我了！放了我吧，凡人！悲伤沼泽……凡人……求你了……', '不要再折磨我了!放了我吧，凡人!悲傷沼澤...凡人...求你了...', '¡No más torturas! ¡Suéltame, mortal! El Pantano de las Penas... mortal... por favor...', '¡No más torturas! ¡Suéltame, mortal! El Pantano de las Penas... mortal... por favor...', 'Не мучь меня больше! Оставь меня, $gсмертный:смертная;! Болото Печали... $gсмертный:смертная;... умоляю...', 0, 0, 0, 0, 'Chained Essence of Eranikus');
INSERT INTO `script_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1001013, 'Will the nightmare ever end?!  I cannot force myself awake!', '악몽은 끝나지 않을 것인가?! 깨어날 수가 없구나!', 'Quand donc ce cauchemar prendra-t-il fin ? Je n\'arriverai pas à rester conscient !', 'Hat dieser Alptraum denn nie ein Ende? Ich kann mich nicht zwingen, wach zu werden!', '什么时候噩梦才能结束？！我不能强迫自己醒过来！', '什麼時候夢魘才能結束?!我不能強迫自己醒過來!', '¡¿Acaso esta pesadilla tendrá fin en algún momento?! ¡No puedo obligarme a estar despierto!', '¡¿Acaso esta pesadilla tendrá fin en algún momento?! ¡No puedo obligarme a estar despierto!', 'Когда же кончится этот кошмар? Я не могу заставить себя пробудиться!', 0, 0, 0, 0, 'Chained Essence of Eranikus');
INSERT INTO `script_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1001012, 'It hurts so much... Itharius, my old friend... please help me...', '너무 고통스럽구나... 이타리우스, 내 오랜 벗이여... 날 도와주게...', 'La douleur est insupportable... Itharius, mon vieil ami... je vous en prie, aidez-moi...', 'Es tut so weh... Itharius, alter Freund... bitte helft mir...', '这对我的伤害太深了……伊萨里奥斯，我的老朋友……请帮帮我……', '這對我的傷害太深了...伊薩里奧斯，我的老朋友...請幫幫我...', 'Duele tanto... Itharius, viejo amigo... por favor, ayúdame...', 'Duele tanto... Itharius, viejo amigo... por favor, ayúdame...', 'Ах, как больно... Итар, мой старый друг... пожалуйста, помоги мне...', 0, 0, 0, 0, 'Chained Essence of Eranikus');
