INSERT INTO `migrations` VALUES ('20170720102227'); 
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000138, 'Corbett, dear.  Harlan needs a load of knitted shirts and pants as soon as we can manage.', '여보. 할란 씨가 가능한 한 빨리 편물 셔츠와 바지 한 짐을 보내 달라는군요.', 'Corbett, très cher. Harlan a besoin d’un tas de chemises et de pantalons tricotés dès que possible.', 'Corbett, mein Teurer. Harlan benötigt so schnell wir können eine Ladung gestrickter Hemden und Hosen.', '亲爱的科贝特，哈兰要我们尽快给他送些衬衣和短裤过去。', '親愛的科貝特，哈蘭要我們儘快給他送些襯衣和束褲過去。', 'Corbett, cariño. Harlan necesita unas cuantas camisas y pantalones de punto cuanto antes.', 'Corbett, cariño. Harlan necesita unas cuantas camisas y pantalones de punto cuanto antes.', 'Корбетт, дорогой. Харлану нужна целая куча вязаных рубашек и штанов – чем скорее, тем лучше.');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000139, 'Corbett, you there?  Harlan needs another load of knitted goods.  Can you take it to him?', '코베트, 듣고 있어요? 할란 씨가 편물 제품을 또 한 짐 부탁했어요. 좀 가져다줄래요?', 'Corbett, c\'est vous ? Harlan a besoin d’un autre tas de tricots. Pouvez-vous les lui apporter ?', 'Corbett, seid Ihr da? Harlan benötigt noch eine Ladung Strickwaren. Könnt Ihr sie ihm bringen?', '科贝特，你在吗？哈兰还需要一些针织品。你能给他带一些过去吗？', '科貝特，你在嗎?哈蘭還需要一些毛衣褲。你能給他帶一些過去嗎?', 'Corbett, ¿estás ahí? Harlan necesita otra carga de ropa de punto. ¿Puedes llevársela?', 'Corbett, ¿estás ahí? Harlan necesita otra carga de ropa de punto. ¿Puedes llevársela?', 'Корбетт, ты тут? Харлану нужна еще одна партия вязаной одежды. Отнесешь ему?');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000140, 'My pleasure, sugar drop.  I\'ll be back soon...', '가고 말고, 여보. 금방 돌아오겠소.', 'Tout le plaisir est pour moi, mon petit sucre. Je reviens bientôt.', 'Es war mir ein Vergnügen, Zuckerschnute. Ich bin bald zurück...', '啊，我很荣幸，小甜心。我很快就回来……', '啊，我很榮幸，小甜心。我很快就回來...', 'Es un placer, preciosa. Pronto volveré...', 'Es un placer, preciosa. Pronto volveré...', 'Разумеется, сладость моя. Я скоро вернусь...');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000141, 'Business must be good down at the bazaar.  I\'ll get him resupplied right away!', '시장에서 장사가 잘 되나 보군. 지금 바로 배달을 다녀오리다.', 'Les affaires doivent bien marcher au bazar. Je vais le réapprovisionner immédiatement !', 'Die Geschäfte im Basar scheinen ja gut zu laufen. Ich werde ihm sofort Nachschub bringen!', '他的生意似乎挺火爆的！我马上就给他补充货物！', '市集的生意一定很棒!我馬上就給他補充貨物!', 'El negocio debe de ir bien en el mercadillo. ¡Tengo que reabastecerlo inmediatamente!', 'El negocio debe de ir bien en el mercadillo. ¡Tengo que reabastecerlo inmediatamente!', 'Дела на базаре, должно быть, идут хорошо. Я сейчас же пополню запасы!');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000142, 'Hm...after dropping this off, I think I\'ll head to that cheese shop for a snack.', '흠... 이걸 배달한 다음에 저 치즈 가게에 들러 뭘 좀 먹어야겠군.', 'Hum… Après avoir déposé ça, je crois que je vais aller faire un tour à cette fromagerie pour manger un morceau.', 'Hm... wenn ich das hier losgeworden bin, werde ich vielleicht in dieses Käsegeschäft gehen und mir dort einen kleinen Imbiss genehmigen.', '嗯……等搞定这些，我就要去奶酪店吃点东西。', '嗯...等貨送好，我就要去乳酪店吃點東西。', 'Mmm... después de entregar esto, me iré a comer un tentempié en esa quesería.', 'Mmm... después de entregar esto, me iré a comer un tentempié en esa quesería.', 'Хм... Как закончим, я схожу в ту сырную лавку и перекушу.');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000143, 'I should have a few extra coins from this sale.  Maybe I\'ll buy myself some lunch...', '돈이 좀 남았군. 이걸로 점심이나 사 먹어야겠어.', 'Je devrais retirer quelques pièces de plus de cette vente. Je pourrais peut-être m’acheter de quoi déjeuner...', 'Ich müsste doch ein paar Münzen bei diesem Geschäft verdient haben. Vielleicht kaufe ich mir ein Mittagessen...', '卖完这些东西我也就能稍微挣点钱了，中午吃什么呢……', '賣完這批東西我也多賺了幾個錢，中午吃什麼呢...', 'Creo que me sobran unas cuantas monedas de esta venta. A lo mejor me compro directamente la comida...', 'Creo que me sobran unas cuantas monedas de esta venta. A lo mejor me compro directamente la comida...', 'После этой сделки у меня должна была остаться пара лишних монет. Купить себе, что ли, на обед что-нибудь...');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000144, 'Hey, Harlan.  Here\'s a load of knitted cloth for you.', '할란 씨, 주문한 편물 옷 한 짐 가져왔습니다.', 'Hé, Harlan. Voici tout un lot de tricots pour toi.', 'He, Harlan. Hier ist eine Ladung Strickstoff für Euch.', '嘿，哈兰，这是你要的衣服。', '嘿，哈蘭，這是你要的衣服。', 'Toma, Harlan. Aquí tienes una carga de ropa de punto.', 'Toma, Harlan. Aquí tienes una carga de ropa de punto.', 'Эй, Харлан. Тут для тебя груз полотна.');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000145, 'Oomph!  Here\'s another load of supplies, Harlan.  It must be selling fast!', '으쌰! 여기 물건 또 한 짐 가져왔습니다! 할란 씨, 장사가 잘 되시나 보네요.', 'Pfff ! Voilà une autre cargaison, Harlan. Ça doit partir vite !', 'Uff! Hier ist eine neue Ladung an Vorräten, Harlan. Euer Geschäft scheint ja gut zu laufen!', '哈兰！这些是你要的另一批衣服，今天你生意真不错啊！', '哈蘭!這些是你要的另一批衣服，今天你生意真不錯啊!', '¡Ea! Aquí tienes otra carga, Harlan. ¡Tiene que venderse rápidamente!', '¡Ea! Aquí tienes otra carga, Harlan. ¡Tiene que venderse rápidamente!', 'Уфф! Вот еще товары, Харлан. Быстро, поди, расходятся?');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000146, 'Ah, much appreciated, Corbett.  We\'ll get these on the racks immediately.', '아, 정말 고맙네, 코베트. 당장 진열해야겠어.', 'Ah, grand merci, Corbett. Nous allons les mettre en rayon immédiatement.', 'Ah, das weiß ich sehr zu schätzen, Corbett. Wir werden alles sofort in die Regale räumen.', '太感谢你了，科贝特。我马上就把这些货物放到柜上出售。', '太感謝你了，科貝特。我馬上就把這些貨物放到櫃上出售。', 'Ah, te lo agradezco mucho, Corbett. Los pondremos en los estantes inmediatamente.', 'Ah, te lo agradezco mucho, Corbett. Los pondremos en los estantes inmediatamente.', 'А, очень вам признателен, Корбетт. Мы пустим их в дело прямо сейчас.');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000147, 'Ah yes, and promptly delivered.  As always, it\'s a pleasure doing business with you, Corbett.', '아, 그래, 빨리도 왔군. 자네와 거래를 하면 언제나 유쾌하단 말이야.', 'Ah oui, et livré rapidement. Comme toujours, c’est un plaisir de traiter avec toi, Corbett.', 'Ah ja, und pünktlich geliefert. Es ist wie immer ein Vergnügen, mit Euch Geschäfte zu machen, Corbett.', '好的，马上发货。很高兴和你做生意，科贝特。', '啊，貨來得真快。很高興和你做生意，科貝特。', 'Ah, sí, y la entrega ha sido rápida. Como de costumbre, es un placer hacer negocios contigo, Corbett.', 'Ah, sí, y la entrega ha sido rápida. Como de costumbre, es un placer hacer negocios contigo, Corbett.', 'Да, и доставлен должным образом. С тобой всегда приятно иметь дело, Корбетт.');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000148, 'Well, I\'m off then.  Take care, Harlan.', '그럼 가보겠습니다. 잘 지내세요, 할란 씨!', 'Bien, je vais m’en aller maintenant. Bonne continuation, Harlem.', 'Dann will ich mal wieder. Macht\'s gut, Harlan.', '好的，那我走了。多多保重，哈兰。', '好的，那我走了。多多保重，哈蘭。', 'Bueno, entonces me marcho. Cuídate, Harlan.', 'Bueno, entonces me marcho. Cuídate, Harlan.', 'Что ж, тогда у меня все. Удачи, Харлан.');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000149, 'Glad to see you\'re doing so well, Harlan.  And I hope to see you again soon...', '장사가 잘 되는 걸 보니 기쁘군요! 할란 씨, 곧 또 보게 되길 바라겠습니다.', 'Content de voir que tu te portes si bien, Harlan. Et j’espère te revoir bientôt...', 'Schön zu sehen, dass es Euch so gut geht, Harlan. Und ich hoffe, wir sehen uns bald wieder...', '很高兴看到你干得这么好，哈兰。希望我们很快能再见面……', '很高興看到你生意這麼好，哈蘭。希望我們很快能再見面...', 'Me alegra saber que te va bien, Harlan. Espero verte pronto de nuevo...', 'Me alegra saber que te va bien, Harlan. Espero verte pronto de nuevo...', 'Рад видеть вас в добром здравии, Харлан. Надеюсь, скоро увидимся...');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000150, 'Now for that snack...', '이제 뭘 좀 먹어 볼까...', 'Maintenant, la collation...', 'Nun zu diesem Imbiss...', '现在可以吃了……', '現在可以吃點心了...', 'Quiero ese tentempié ahora...', 'Quiero ese tentempié ahora...', 'А теперь закусим...');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000151, 'Good day, Elling!  Hullo Elaine!  Let me have a wheel of bleu cheese, eh?', '안녕하세요, 엘링 씨! 잘 지내셨나요, 일레인 씨! 푸른 치즈 한 덩이만 주시겠어요?', 'Bonjour, Elling ! B’jour Elaine ! Mets-moi une meule de bleu, tu veux bien ?', 'Guten Tag, Elling! Hallo Elaine! Kann ich einen Laib Blaukäse bekommen?', '你好，埃林！你好啊，艾莱尼！我要来点奶酪。', '你好，伊林!你好啊，伊萊恩!我要來點乳酪。', '¡Buenos días, Elling! ¡Hola, Elaine! ¿Puedo coger un trocito de queso azul?', '¡Buenos días, Elling! ¡Hola, Elaine! ¿Puedo coger un trocito de queso azul?', 'Добрый день, Иллинг! Привет, Элайна! Можно мне головку сыра, а?');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000152, 'Hullo, Trias clan!  A ball of your smoked mozzarella, if you please!', '안녕하세요, 트리아스가 여러분! 제일 좋은 푸른 치즈 한 덩이만 주세요!', 'Salut, les Trias ! Un peu de votre mozzarella fumée, s\'il vous plaît !', 'Hallo, Triasklan! Eine Kugel von Eurem geräucherten Mozzarella, bitte!', '你好啊，提亚斯！我要买些奶酪。', '你好啊，提亞斯!給我來一份煙燻乳酪球。', '¡Hola, clan de los Trias! ¡Coge una bola de mozzarella ahumada, por favor!', '¡Hola, clan de los Trias! ¡Coge una bola de mozzarella ahumada, por favor!', 'Привет, клан Триаса! Мне, пожалуйста, шар вашей копченой моцареллы!');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000153, 'Good day, Corbett.  Here\'s your cheese, fresh made this morning!  And how are things at your shop?', '안녕하세요, 코베트 씨! 자, 치즈 여기 있어요. 오늘 아침에 막 만든 거랍니다. 가게는 잘 되시고요?', 'Bonjour, Corbett. Voici votre fromage, frais de ce matin ! Comment vont les affaires dans votre boutique ?', 'Guten Tag, Corbett. Hier ist Euer Käse. Heute Morgen ganz frisch gemacht! Und wie läuft Euer Laden?', '你好，科贝特。这是今天早上刚做好的奶酪！你的商店生意还好么？', '你好，科貝特。這是今天早上剛做好的乳酪!你的商店生意還好麼?', '¡Buenos días, Corbett! ¡Aquí tienes tu queso, fresco de esta mañana! ¿Cómo van las cosas en tu tienda?', '¡Buenos días, Corbett! ¡Aquí tienes tu queso, fresco de esta mañana! ¿Cómo van las cosas en tu tienda?', 'Добрый день, Корбетт. Вот ваш сыр, только сегодня сделан. Как дела в магазине?');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000154, 'Hi Corbett!  Here, you go!  I trust business is faring well at your clothier shop...?', '코베트씨, 어서 오세요! 자, 여기 있어요. 옷 가게는 잘 되고 있죠?', 'Bonjour, Corbett ! Voilà ! J’espère que les affaires se portent bien dans votre boutique de vêtements...?', 'Hallo, Corbett! Bitte sehr! Sehe ich es richtig, dass Euer Tuchladen gut läuft...?', '你好啊，科贝特！拿着吧！你服装店的生意一定很不错吧？', '你好啊，科貝特!拿著吧!你服裝店的生意一定很不錯吧?', '¡Hola, Corbett! ¡Cómo vas! ¿El negocio va bien en tu tienda de ropa?', '¡Hola, Corbett! ¡Cómo vas! ¿El negocio va bien en tu tienda de ropa?', 'Эй, Корбетт! Вот ты где! Как там твоя портняжная мастерская? Все так же процветает?..');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000155, 'Yes ma\'am, business is brisk!', '정말, 장사가 아주 잘 되더라고!', 'Oui, m’dame, les affaires marchent bien !', 'Ja, gnädige Frau, das Geschäft läuft gut!', '是啊是啊，生意很忙！', '是啊是啊，生意很忙!', '¡Sí, señora, el negocio va viento en popa!', '¡Sí, señora, el negocio va viento en popa!', 'Да, мэм, дело быстрое!');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000156, 'Thank you kindly!', '정말 고맙습니다!', 'Merci bien !', 'Recht herzlichen Dank!', '没问题，非常感谢你！', '沒問題，非常感謝你!', '¡Muchísimas gracias!', '¡Muchísimas gracias!', 'Большое тебе спасибо!');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000157, 'Thanks for the cheese!', '치즈 고맙습니다!', 'Merci pour le fromage !', 'Danke für den Käse!', '我会的，谢谢你的奶酪！', '我會的，謝謝你的乳酪!', '¡Gracias por el queso!', '¡Gracias por el queso!', 'Спасибо за сыр!');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000158, 'See you later, Corbett, and tell the missus hello!', '또 봅시다, 코베트 씨. 부인께 안부 전해 주시오!', 'À plus tard, Corbett, et donnez mon bonjour à votre p’tite dame !', 'Bis später, Corbett, und grüßt Eure bessere Hälfte von mir!', '再见，科贝特，代我向你妻子问好！', '再見，科貝特，代我向你妻子問好!', 'Hasta pronto, Corbett, y saluda a las señoras de mi parte.', 'Hasta pronto, Corbett, y saluda a las señoras de mi parte.', 'До встречи, Корбетт, и жене привет передавай!');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000159, 'Take care, and send our regards to your family.', '잘 가시오. 가족에게 안부 전해 주시오.', 'Bonne continuation et bonjour à la famille de notre part.', 'Macht\'s gut, und grüßt Eure Familie.', '保重，代我向你家人问好。', '保重，代我向你家人問好。', 'Cuídate y manda saludos a tu familia.', 'Cuídate y manda saludos a tu familia.', 'Береги себя – и передавай привет семье.');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000160, 'I should get back before Rema starts to worry...', '레마가 걱정하기 전에 빨리 돌아가야겠군.', 'Je devrais partir avant que Rema ne commence à se faire du souci...', 'Ich sollte zurückgehen, bevor Rema sich Sorgen macht...', '我得在雷玛开始担心之前回去……', '我得在瑞瑪開始擔心之前回去...', 'Debería regresar antes de que Rema empiece a preocuparse...', 'Debería regresar antes de que Rema empiece a preocuparse...', 'Мне надо вернуться, а то Рема начнет беспокоиться...');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000161, 'Time to get back to the shop...', '가게에 돌아가야 할 시간이군.', 'Il est temps de retourner à la boutique...', 'Zeit, wieder zurück in den Laden zu gehen...', '该回商店了……', '該回店裡了...', 'Es hora de volver al tajo...', 'Es hora de volver al tajo...', 'Пора вернуться в лавку...');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000000162, 'I\'m back!', '다녀왔소!', 'Je suis de retour !', 'Ich bin zurück!', '我回来了！', '我回來了!', '¡Estoy de vuelta!', '¡Estoy de vuelta!', 'Вот и я!');
