INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000007004, 'Arrr, Me Hearties!   I be havin\' some extra Treasure that I be givin\' away at the Gurubashi Arena!  All ye need do to collect it is open the chest I leave on the arena floor!', '어이, 친구들! 다들 잘 들어! 내가 남은 보물을 나눠 줄게! 구루바시 투기장에 궤짝을 놔둘 테니 가져가라고! 먼저 여는 자가 임자인 거 알지?', 'Salut, les p\'tits gars ! J’ai quelques trésors en trop, alors je les donne à l’arène des Gurubashi ! Tout ce que vous avez à faire, c’est d’ouvrir le coffre que je laisse sur le sol de l’arène !', 'Arr, meine Herzchen! Ich habe noch einige weitere Schätze in der Gurubashi Arena abzugeben! Ihr müsst nur die von mir in der Mitte der Arena zurückgelassene Truhe öffnen.', '啊，我亲爱的朋友们！我在古拉巴什竞技场放了一些宝贝！你们要做的只是去竞技场中央，打开箱子，然后拿到里面的财宝！', '啊，我親愛的!我把多出來的財寶放在了古拉巴什競技場!你要做的只是去競技場中央，打開箱子，然後拿到裡面的財寶!', '¡Arr, compañeros! ¡Yo tener algunos tesoros extra que dar en la Arena Gurubashi! ¡Vosotros solo tener que abrir el cofre que yo dejar en el suelo de la arena!', '¡Arr, compañeros! ¡Yo tener algunos tesoros extra que dar en la Arena Gurubashi! ¡Ustedes solo tener que abrir el cofre que yo dejar en el suelo de la arena!', 'Арр, бей-круши! А у меня есть лишние сокровища – прямо на арене Гурубаши! Чтобы их взять, тебе надо открыть сундук, который я оставил на ринге арены!');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000007005, 'Let the Bloodletting Begin!', '피 튀기는 싸움을 시작하자!', 'Que le Carnage commence !', 'Lasst das Blutvergießen beginnen!', '开始厮杀吧！', '開始放血吧!', '¡Que comience el combate a muerte!', '¡Que comience el combate a muerte!', 'Пускай кровь льется рекой!');
