INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (3651, 0, '여명의 계략을 하나 더 주시겠어요, 베티나? 다시 시험해 보고 싶어요.', 'Je peux avoir un autre Gambit de l\'aube, Betina ? Je veux encore le tester.', 'Gebt Ihr mir noch eine Dämmerungstrickfalle, Betina? Ich möchte sie noch einmal ausprobieren...', '贝蒂娜，我能再要一个黎明先锋吗？我想再次测试一下……', '貝蒂娜，我能再要一個黎明先鋒嗎?我想再次測試一下...', '¿Puedes darme otro Gambito del Alba, Betina? Quiero intentarlo otra vez...', '¿Puedes darme otro Gambito del Alba, Betina? Quiero intentarlo otra vez...', 'Могу ли я попросить еще один "Рассветный гамбит", Бетина? Мне нравится его вкус...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (3651, 1, '베티나, 여명의 문장으로 교체해 주세요!', 'Betina, j\'ai besoin d\'un nouveau Sceau de l\'Aube, s\'il vous plaît.', 'Betina, bitte gebt mir erneut ein Siegel der Dämmerung.', '贝蒂娜，我想要黎明之印的替代物品！', '貝蒂娜，我想要黎明聖印的替代品!', '¡Betina, necesito un repuesto del Sello del Alba, por favor!', '¡Betina, necesito un repuesto del Sello del Alba, por favor!', 'Бетина, мне нужна новая печать Рассвета.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (3651, 2, '베티나, 여명의 룬으로 교체해 주세요!', 'Betina, j\'ai besoin d\'une nouvelle Rune de l\'Aube, s\'il vous plaît.', 'Betina, bitte gebt mir erneut eine Rune der Dämmerung!', '贝蒂娜，我想要黎明符文的替代物品！', '貝蒂娜，我想要黎明符文的替代品!', '¡Betina, necesito un repuesto de la Runa del Alba, por favor!', '¡Betina, necesito un repuesto de la Runa del Alba, por favor!', 'Бетина, мне нужна запасная руна Рассвета!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
