INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000007029, 'Ahhhh!!! What is that thing?!', '으아아악!!! 저게 뭐지?', 'Ahhhh ! Qu’est-ce que c’est ?!', 'Ahhhh!!! Was ist das denn?!', '啊！！！那是什么东西？！', '啊!!!那是什麼東西?!', '¡¡¡Ahhhh!!! ¡¿Qué es eso?!', '¡¡¡Ahhhh!!! ¡¿Qué es eso?!', 'А-а-а-а!!! Что это?!');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000007030, 'Umi sent you, didn\'t she?! She told me to expect a surprise, but I never thought that this is what she meant!', '우미가 보낸 거 맞죠? 평소에 절 깜짝 놀라게 해 주겠다고 말했지만, 이런 것일 줄은 생각도 못 했어요!', 'Umi vous envoie, n’est-ce pas ? Elle m’a dit de m’attendre à une surprise, mais je n’aurais jamais cru que c’était de cela dont elle parlait !', 'Umi hat Euch geschickt, oder? Sie hat mir gesagt, ich solle mich auf eine Überraschung gefasst machen, aber ich hatte keine Ahnung, dass sie das gemeint hat!', '乌米派你来的，是吗？！她告诉我会有一个惊喜，但是我从没想到她是这个意思！', '烏米派你來的，是嗎?!她告訴我會有一個驚喜，但是我從沒想到她是這個意思!', '¡Umi te ha enviado, ¿verdad?! Me dijo que esperara una sorpresa, ¡pero nunca pensé que se refiriera a esto!', '¡Umi te ha enviado, ¿verdad?! Me dijo que esperara una sorpresa, ¡pero nunca pensé que se refiriera a esto!', 'Так тебя Уми прислала, да?! Она обещала мне сюрприз, но я бы в жизни не подумала, что он будет таким!');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000007031, 'Ahhhhh!!!', '으아아악!!!', 'Ahhhhh !', 'Aaaahhh!', '啊！！！', '啊!!!', '¡¡¡Ahhhhh!!!', '¡¡¡Ahhhhh!!!', 'Аххххх!!!');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000007032, 'You big meanie! Who put you up to this?', '어떻게 이럴 수가 있죠! 누가 이런 짓을 시킨 거예요?', 'Ah, vous alors ! Qui vous a dit de faire ça ?', 'Das ist gemein! Wer hat Euch dazu angestiftet?', '你个尖酸的刻薄鬼！谁把这个给你的？', '你個尖酸的刻薄鬼!誰把這個給你的?', '¡Eh, tacaño! ¿Quién te dio la idea?', '¡Eh, tacaño! ¿Quién te dio la idea?', 'Ах ты, $gнегодяй:негодяйка;! Кто тебя на это подбил?');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000007033, 'It was Umi, wasn\'t it?! She\'s always playing jokes on me, and now she\'s got you in on it too!', '우미가 시킨 거죠? 항상 나한테 장난을 치더니, 이제는 당신까지 끌어들였군요!', 'C’est Umi, n’est-ce pas ? Elle ne cesse de me jouer des tours et maintenant vous vous y mettez aussi !', 'Bestimmt Umi, oder? Die spielt mir ständig irgendwelche Streiche, und jetzt hat sie auch noch Euch mit eingespannt!', '是乌米，是吗？？她总是跟我开玩笑，现在也让你参与到玩笑之中了！', '是烏米，是嗎??她總是跟我開玩笑，現在也讓你參與到玩笑之中了!', '¡Fue Umi, ¿verdad?! Siempre me está gastando bromas, ¡y ahora también te la ha jugado a ti!', '¡Fue Umi, ¿verdad?! Siempre me está gastando bromas, ¡y ahora también te la ha jugado a ti!', 'Это была Уми, да?! Она постоянно разыгрывает меня, а теперь вот и до тебя добралась!');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000007034, '%s sighs.', '%s|1이;가; 한숨을 쉽니다.', '%s soupire.', '%s seufzt.', '%s叹了口气。', '%s嘆了一口氣。', '%s suspira.', '%s suspira.', '%s вздыхает.');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000007035, '%s runs quickly away from Umi\'s Mechanical Yeti.', '%s|1이;가; 재빨리 우미의 기계설인으로부터 멀리 달아납니다.', '%s s\'enfuit à toute vitesse devant le yéti mécanique d\'Umi.', '%s läuft schnell vor Umis mechanischem Yeti weg.', '%s迅速从乌米的机械雪人面前逃跑。', '%s迅速逃離烏米的機械雪人。', '%s huye corriendo del yeti mecánico de Umi.', '%s huye corriendo del yeti mecánico de Umi.', '%s стремглав убегает от механического йети Уми.');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000007036, 'Why do you chase me, Mechanical Yeti?! WHY?!', '기계설인아, 왜 날 쫓아오는 거니? 도대체 왜?', 'Pourquoi me chasses-tu, yéti mécanique, hein ? POURQUOI ?', 'Warum verfolgst du mich, mechanischer Yeti?! WARUM?!', '你为什么要追我，机械雪人？！为什么？！', '你為什麼要追我，機械雪人?!為什麼?!', '¡¿Por qué me persigues, yeti mecánico?! ¡¿POR QUÉ?!', '¡¿Por qué me persigues, yeti mecánico?! ¡¿POR QUÉ?!', 'Почему ты гонишься за мной, механический йети? ПОЧЕМУ?!');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000007037, '%s looks relieved.', '%s|1이;가; 안심한 것 같습니다.', '%s semble soulagé.', '%s sieht erleichtert aus.', '%s看上去很轻松。', '%s看起來鬆了一口氣。', '%s mira con alivio.', '%s mira con alivio.', '%s выглядит успокоенным.');
INSERT INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES (2000007038, 'I\'m jumpy as it is... and people insist on scaring me... Next time, though, I\'ll be ready!', '그렇지 않아도 심장이 약한 편이라 잘 놀라는데... 자꾸만 놀라게 하는군... 하지만 다음번에는 단단히 준비하고 있을 거야!', 'Je suis impressionnable, et les gens s\'amusent à me faire sursauter... Mais la prochaine fois, je ne me laisserai pas faire !', 'Ich bin doch ohnehin schon schreckhaft genug... und trotzdem wollen mich die Leute dauernd erschrecken... Aber beim nächsten Mal bin ich vorbereitet!', '我总是战战兢兢……人们还在不断吓我……但是下次，我会有所准备的！', '我總是戰戰兢兢...人們還在不斷嚇我...但是下次，我會有所準備的!', 'Yo siempre estoy de los nervios... y la gente se empeña en asustarme... ¡Pero la próxima vez estaré preparado!', 'Yo siempre estoy de los nervios... y la gente se empeña en asustarme... ¡Pero la próxima vez estaré preparado!', 'Я ужасно нервный... и всем нравится меня пугать... Но в следующий раз я буду готов!');
