INSERT INTO `creature_ai_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1213, 'SMOTTS!', '스모츠!', 'SMOTTS !', 'SMOTTS!', '斯莫特！', '斯莫特!', '¡SMOTTS!', '¡SMOTTS!', 'СМОТТС!', 0, 1, 0, 0, 'Mok\'Rash - Random Yell');
INSERT INTO `creature_ai_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1212, 'YOU WERE SUCH GOOD SPORT THE LAST TIME...COME OUT!', '지난번에 널 데리고 놀았을 때 아주 재밌었다... 자, 어서 나와!', 'TU AS ÉTÉ TELLEMENT BON JOUEUR LA DERNIÈRE FOIS… SORS DONC !', 'DAS LETZTE MAL WART IHR SO EIN GUTER KERL... KOMMT RAUS!', '你上次给我带来不少乐子哪……出来吧！', '你上次給我帶來不少樂子哪...出來吧!', 'FUISTE MUY DIVERTIDO LA ÚLTIMA VEZ... ¡SAL!', 'FUISTE MUY DIVERTIDO LA ÚLTIMA VEZ... ¡SAL!', 'В ТОТ РАЗ МЫ ТАК СЛАВНО ПОВЕСЕЛИЛИСЬ... ВЫХОДИ ЖЕ!', 0, 1, 0, 0, 'Mok\'Rash - Random Yell');
INSERT INTO `creature_ai_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1211, 'HAVE YOU RUN OUT OF SHIPS TO SEND?  WHERE\'S YOUR COURAGE?', '이제 보낼 배가 더는 없는 건가? 너의 그 가상한 용기는 다 어디 간 거야?', 'TU N’AS PLUS DE NAVIRES À ENVOYER ? OÙ EST TON COURAGE ?', 'HABT IHR KEINE SCHIFFE MEHR, DIE IHR SCHICKEN KÖNNT? HAT EUCH DER MUT VERLASSEN?', '你一艘船都没有了吗？你的勇气去哪里了？', '你一艘船都沒有了嗎?你的勇氣去哪裡了?', '¿YA NO TIENES MÁS NAVES QUE ENVIAR? ¿DÓNDE QUEDÓ TU CORAJE?', '¿YA NO TIENES MÁS NAVES QUE ENVIAR? ¿DÓNDE QUEDÓ TU CORAJE?', 'У ТЕБЯ ЧТО, КОРАБЛИ ЗАКОНЧИЛИСЬ? ГДЕ ЖЕ ТВОЯ СМЕЛОСТЬ?', 0, 1, 0, 0, 'Mok\'Rash - Random Yell');
INSERT INTO `creature_ai_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1210, 'I PROMISE I WON\'T KILL YOUR CREW THIS TIME.  HAH!  BUT MY BROTHER MIGHT EAT THEM!', '이번에는 네 동료를 죽이지 않겠다고 약속할게. 흐흐! 하지만 내 동생이 먹어 버릴지도 몰라!', 'JE JURE QUE JE NE TUERAI PAS TON ÉQUIPAGE CETTE FOIS-CI. HA ! MAIS MON FRÈRE LE MANGERA PEUT-ÊTRE !', 'ICH VERSPRECHE, DIESMAL WERDE ICH EURE MANNSCHAFT NICHT TÖTEN. HA! ABER MEIN BRUDER KÖNNTE SIE AUFESSEN!', '我保证这次我不会杀死你的船员，哈哈！但是我的兄弟可能会吃了他们！', '我保證這次我不會殺死你的船員，哈哈!但是我的兄弟可能會吃了他們!', 'TE PROMETO QUE ESTA VEZ NO MATARÉ A TU TRIPULACIÓN. ¡JA! ¡PERO MI HERMANO PODRÍA COMÉRSELOS!', 'TE PROMETO QUE ESTA VEZ NO MATARÉ A TU TRIPULACIÓN. ¡JA! ¡PERO MI HERMANO PODRÍA COMÉRSELOS!', 'ОБЕЩАЮ, ЧТО В ЭТОТ РАЗ Я ТВОЮ КОМАНДУ НЕ УБЬЮ. ХА! НО ВОТ БРАТ МОЙ МОЖЕТ ЕЮ ПОЛАКОМИТЬСЯ!', 0, 1, 0, 0, 'Mok\'Rash - Random Yell');
INSERT INTO `creature_ai_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1209, 'COME OUT TO JANEIRO\'S POINT.  I\'M WAITING FOR YOU, SMOTTS...', '자네이로 섬으로 나와. 기다리고 있겠다, 스모츠.', 'VIENS JUSQU’AU CAP JANEIRO. JE T’ATTENDS, SMOTTS...', 'KOMMT ZUR JANEIROSPITZE. ICH WARTE AUF EUCH, SMOTTS...', '出来，到加尼罗哨站来。我等着你，斯莫特……', '出來，到加尼祿哨站來。我等著你，斯莫特...', 'SAL AL CAYO DE JANEIRO. TE ESTOY ESPERANDO SMOTTS...', 'SAL AL CAYO DE JANEIRO. TE ESTOY ESPERANDO SMOTTS...', 'ВЫХОДИ К ВЕРШИНЕ ЖАНЕЙРО. Я ЖДУ ТЕБЯ, СМОТТС...', 0, 1, 0, 0, 'Mok\'Rash - Random Yell');
INSERT INTO `creature_ai_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1208, 'DON\'T LET THIS STATUE BE MY ONLY COMPANY.  COME OUT AND PLAY!', '이 조각상하고만 노는 것도 지겨워졌다고. 나와서 붙어보잔 말이야!', 'NE ME LAISSE PAS SEUL EN COMPAGNIE DE CETTE STATUE. VIENS JOUER !', 'LASST DIESE STATUE NICHT MEINE EINZIGE GESELLSCHAFT SEIN. KOMMT HERAUS UND SPIELT!', '我可不想整天陪着这座雕像玩。过来较量较量吧！', '我可不想整天陪著這座雕像玩。過來較量較量吧!', 'NO DEJÉIS QUE LA ESTATUA SEA MI ÚNICA COMPAÑÍA. ¡SALID A JUGAR!', 'NO DEJÉIS QUE LA ESTATUA SEA MI ÚNICA COMPAÑÍA. ¡SALID A JUGAR!', 'МНЕ СКУЧНО В КОМПАНИИ ЭТОЙ СТАТУИ. ВЫХОДИ, ПОИГРАЕМ!', 0, 1, 0, 0, 'Mok\'Rash - Random Yell');
INSERT INTO `creature_ai_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1207, 'SMOTTS!  HEY, SMOTTS!!  COME OUT AND PLAY!', '스모츠! 이봐, 스모츠! 나와서 붙어 보자고!', 'SMOTTS ! HÉ, SMOTTS !! DEHORS, ON JOUE !!', 'SMOTTS! HE, SMOTTS! KOMMT HERAUS UND SPIELT!', '斯莫特！嘿，斯莫特！出来玩玩吧！', '斯莫特!嘿，斯莫特!出來玩玩吧!', '¡SMOTTS! ¡¡OYE, SMOTTS!! ¡SAL A JUGAR!', '¡SMOTTS! ¡¡OYE, SMOTTS!! ¡SAL A JUGAR!', 'СМОТТС! ЭЙ, СМОТТС!!! ВЫХОДИ, ПОИГРАЕМ!', 0, 1, 0, 0, 'Mok\'Rash - Random Yell');
INSERT INTO `creature_ai_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-1206, 'DON\'T YOU WANT TO FIGHT, SMOTTS!?', '스모츠, 싸우고 싶지 않나?', 'TU NE VEUX PAS TE BATTRE, SMOTTS ?!', 'WOLLT IHR NICHT KÄMPFEN, SMOTTS!?', '斯莫特，你不想战斗吗？', '你不是想打嗎，斯莫特!?', '¿NO QUIERES LUCHAR, SMOTTS?', '¿NO QUIERES LUCHAR, SMOTTS?', 'НЕ ХОЧЕШЬ СРАЗИТЬСЯ, СМОТТС?!', 0, 1, 0, 0, 'Mok\'Rash - Random Yell');
